function CargarAlertas(){$.get("/Base/CargarAlertas",function(n){$("#AlertsContainer").append(n)},"html");ShowToast()}function Danger(n){var t=ToastTemplate(n,"error");$(".toast-container").append(t);ShowToast()}function Info(n){var t=ToastTemplate(n,"info");$(".toast-container").append(t);ShowToast()}function Warning(n){var t=ToastTemplate(n,"warning");$(".toast-container").append(t);ShowToast()}function Success(n){var t=ToastTemplate(n,"success");$(".toast-container").append(t);ShowToast()}function ToastTemplate(n,t){var i="Info";switch(t){case"success":i="Success!";break;case"info":i="Info";break;case"warning":i="Warning";break;case"error":i="Error";className="toast-error"}return'<div class="toast toast-'+t+'" role="alert" aria-live="assertive" aria-atomic="true">                 <div class="toast-header">                     <strong class="me-auto">'+i+'<\/strong>                     <button type="button" class="btn-close" data-bs-dismiss="toast" aria-label="Close"><\/button>                 <\/div>                 <div class="toast-body">'+n+"<\/div>               <\/div>"}function ShowToast(){var n=[].slice.call(document.querySelectorAll(".toast:not(.hide)")),t=n.map(function(n){return new bootstrap.Toast(n)});$(".toast:not(.hide)").on("hidden.bs.toast",function(){$(this).remove()});t.forEach(n=>n.show())}