function CargarAlertas(n){$("#Alerts"+n).load("/Base/CargarAlertas",{keyName:[n,"Combos"]})}function Danger(n){new PNotify({text:n,type:"error",styling:"bootstrap3"})}function Info(n){new PNotify({text:n,type:"info",styling:"bootstrap3"})}function Warning(n){new PNotify({text:n,type:"warning",styling:"bootstrap3"})}function Success(n){new PNotify({text:n,type:"success",styling:"bootstrap3"})}